import React, { useState, useEffect } from 'react';
import './HomePage.css';
import axios from 'axios';

function HomePage() {
  const [scenarios, setScenarios] = useState([]);

  useEffect(() => {
    // Fetch scenarios data when component mounts
    fetchScenarios();
  }, []);

  const fetchScenarios = async () => {
    try {
      const response = await axios.get('/scenarios');
      setScenarios(response.data);
    } catch (error) {
      console.error('Error fetching scenarios:', error);
    }
  };

  const handleStartSimulation = (scenarioId) => {
    // Start simulation logic
  };

  return (
    <div className="homepage">
      <h1>Scenarios</h1>
      {scenarios.map((scenario) => (
        <div className="scenario" key={scenario.id}>
          <h2>{scenario.name}</h2>
          <p>Time: {scenario.time}</p>
          <button onClick={() => handleStartSimulation(scenario.id)}>Start Simulation</button>
        </div>
      ))}
      {/* Simulation container */}
      <div className="simulation-container">
        {/* Display vehicles moving here */}
      </div>
    </div>
  );
}

export default HomePage;
